import React, { useState } from "react";
import Shadows from "../components/Shadows";

const Impressum = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  return (
    <div>
      <div className="hero">
        <Shadows />
        <h1 className="main-heading">Impressum</h1>
        <p className="subheading">
          Innovating without limits, empowering your vision
        </p>
      </div>

      {/* Thumbnail */}
      <div
        onClick={openModal}
        style={{
          margin: "30px auto",
          maxWidth: "300px",
          cursor: "pointer",
          textAlign: "center",
        }}
      >
        <img
          src="path/to/thumbnail-image.jpg"
          alt="Impressum Thumbnail"
          style={{
            width: "100%",
            borderRadius: "8px",
            boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
          }}
        />
        <p>Click to view full document</p>
      </div>

      {/* Modal */}
      {isModalOpen && (
        <div
          onClick={closeModal}
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100vw",
            height: "100vh",
            backgroundColor: "rgba(0, 0, 0, 0.8)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000,
          }}
        >
          <div
            onClick={(e) => e.stopPropagation()}
            style={{
              backgroundColor: "#fff",
              padding: "20px",
              borderRadius: "10px",
              maxWidth: "90%",
              maxHeight: "90%",
              overflowY: "auto",
              textAlign: "left",
            }}
          >
            <button
              onClick={closeModal}
              style={{
                background: "none",
                border: "none",
                fontSize: "20px",
                cursor: "pointer",
                position: "absolute",
                top: "10px",
                right: "20px",
              }}
            >
              &times;
            </button>
            <h1>Impressum</h1>
            <p>
              Protecht AG, Managing Director Muhamer Nuridini <br />
              Zugerstrasse 72, 6340 Baar, +41767354845, office@protecht.ch
            </p>
            <h3>Disclaimer</h3>
            <p>
              The author assumes no liability for the correctness, accuracy,
              timeliness, reliability, and completeness of the information
              provided.
            </p>
            <h3>Liability for Links</h3>
            <p>
              References and links to third-party websites lie outside our area
              of responsibility. Any responsibility for such websites is
              declined. Access to and use of such websites is at the user’s own
              risk.
            </p>
            <h3>Copyrights</h3>
            <p>
              The copyright and all other rights to content, images, photos, or
              other files on the website belong exclusively to Protecht AG or
              the specifically mentioned copyright holders. The written consent
              of the copyright holder must be obtained in advance for the
              reproduction of any elements.
            </p>
            <p>Baar, November 25, 2024</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Impressum;
