const Testimonials = () => {
  const [currentIndex, setCurrentIndex] = useState(testimonials.length); // Start at first real slide
  const [isTransitioning, setIsTransitioning] = useState(false);

  // Full list with cloned slides
  const clonedTestimonials = [
    testimonials[testimonials.length - 1], // Clone of the last slide
    ...testimonials,
    testimonials[0], // Clone of the first slide
  ];

  const slideWidth = 100 / testimonials.length; // Width of each slide

  const handleNext = () => {
    if (!isTransitioning) {
      setIsTransitioning(true);
      setCurrentIndex((prevIndex) => prevIndex + 1);
    }
  };

  const handlePrev = () => {
    if (!isTransitioning) {
      setIsTransitioning(true);
      setCurrentIndex((prevIndex) => prevIndex - 1);
    }
  };

  const handleTransitionEnd = () => {
    setIsTransitioning(false);

    // Reset index seamlessly when reaching clones
    if (currentIndex === 0) {
      setCurrentIndex(testimonials.length); // Go to the last real slide
    } else if (currentIndex === clonedTestimonials.length - 1) {
      setCurrentIndex(1); // Go to the first real slide
    }
  };

  return (
    <div className="container2">
      <div className="slider-header">
        <h2 className="slider-title">
          Hear from our <span className="title-highlight">customers</span>
        </h2>
        <div className="button-group">
          <button onClick={handlePrev} className="navigation-button">
            <ChevronLeft className="navigation-icon" />
          </button>
          <button onClick={handleNext} className="navigation-button">
            <ChevronRight className="navigation-icon" />
          </button>
        </div>
      </div>

      <div className="testimonial-wrapper">
        <div
          className="testimonial-grid"
          style={{
            transform: `translateX(-${currentIndex * slideWidth}%)`,
            transition: isTransitioning ? "transform 0.5s ease" : "none", // Disable animation during reset
          }}
          onTransitionEnd={handleTransitionEnd}
        >
          {clonedTestimonials.map((testimonial, index) => (
            <div key={index} className="testimonial-card">
              <div className="testimonial-header">
                <img
                  src={testimonial.image}
                  alt={testimonial.name}
                  className="testimonial-image"
                />
                <div>
                  <h3 className="testimonial-name">{testimonial.name}</h3>
                  <p className="testimonial-role">
                    {testimonial.role} at {testimonial.company}
                  </p>
                </div>
              </div>
              <p className="testimonial-quote">"{testimonial.quote}"</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Testimonials;
