import React, { useState } from "react";
import ImpressumImg from "../images/Impressum.jpg"; // Import your image file

const Impressum = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <div>
      <h1>Impressum</h1>
      {/* Thumbnail to open the modal */}
      <div
        style={{
          cursor: "pointer",
          display: "inline-block",
          margin: "20px",
          border: "1px solid #ccc",
          padding: "10px",
          borderRadius: "8px",
          backgroundColor: "#f9f9f9",
        }}
        onClick={toggleModal}
      >
        <img
          src={ImpressumImg} // Thumbnail image
          alt="Impressum Thumbnail"
          style={{ display: "block", width: "100%", borderRadius: "8px" }}
        />
      </div>

      {/* Full-screen modal */}
      {isModalOpen && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.7)",
            zIndex: 1000,
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
          onClick={toggleModal} // Close modal when clicking on background
        >
          <img
            src={ImpressumImg} // Full-screen image
            alt="Impressum"
            style={{
              maxWidth: "100%",
              maxHeight: "100%",
              width: "auto",
              height: "auto",
            }}
          />
        </div>
      )}
    </div>
  );
};

export default Impressum;
