import React, { useState } from "react";
import ImpressumImg from "../images/Impressum.jpg"; // Path to your image
import "../styles_css/Impressum.css";

const Impressum = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Function to toggle modal visibility
  const toggleModal = (e) => {
    e.stopPropagation(); // Prevent propagation for nested clicks
    setIsModalOpen(!isModalOpen);
  };

  return (
    <div>
      {/* Thumbnail Image */}
      <div className="bigcont" onClick={toggleModal}>
        <img
          src={ImpressumImg}
          alt="Impressum Thumbnail"
          style={{ display: "block", width: "100%", borderRadius: "8px" }}
        />
      </div>

      {/* Modal */}
      {isModalOpen && (
        <div
          className="modal-overlay"
          onClick={() => setIsModalOpen(false)} // Close modal when clicking on background
        >
          {/* Close Button */}
          <button
            className="close-button"
            onClick={toggleModal} // Close modal when clicking the button
          >
            &times;
          </button>

          {/* Full Image */}
          <img
            src={ImpressumImg}
            alt="Impressum"
            className="modal-image"
            onClick={(e) => e.stopPropagation()} // Prevent modal close when clicking on the image
          />
        </div>
      )}
    </div>
  );
};

export default Impressum;
