// Contact.js
import React from "react";
import {
  Phone,
  Mail,
  MapPin,
  Linkedin,
  Facebook,
  Instagram,
} from "lucide-react";
import "../styles_css/Contact.css";

const Contact = () => {
  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission
  };

  return (
    <div className="contact-page">
      <div className="container">
        {/* Left Container */}
        <div className="contact-info">
          <h1>
            <span className="highlight">We'd Love to</span> Hear From You
          </h1>
          <p>
            For inquiries or further assistance, please contact us through our
            website or reach out directly via email or phone.
          </p>

          <div className="info-list">
            <div className="info-item">
              <Phone className="icon" />
              <span>+41767354845</span>
            </div>
            <div className="info-item">
              <Mail className="icon" />
              <span>office@protecht.ch</span>
            </div>
            <div className="info-item">
              <MapPin className="icon" />
              <div>
                <p>Zugerstrasse 72, 6340 Baar</p>
                <p>Switzerland</p>
              </div>
            </div>
          </div>

          <div className="social-links">
            <a
              href="https://www.linkedin.com/company/protechtch/posts/?feedView=all"
              className="social-icon"
            >
              <Linkedin className="icon" />
            </a>
            <a
              href="https://www.facebook.com/protecht.ch/"
              className="social-icon"
            >
              <Facebook className="icon" />
            </a>
            <a
              href="https://www.instagram.com/protecht_ch"
              className="social-icon"
            >
              <Instagram className="icon" />
            </a>
          </div>
        </div>

        {/* Right Container */}
        <div className="contact-form">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <input type="text" placeholder="First name" required />
              <input type="text" placeholder="Last name" required />
            </div>
            <input type="email" placeholder="Email" required />
            <input type="tel" placeholder="Phone number" />
            <textarea placeholder="Leave a message..." rows="6"></textarea>
            <button type="submit">Send message</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Contact;
