// Contact.js
import React from "react";
import {
  Phone,
  Mail,
  MapPin,
  Linkedin,
  Facebook,
  Instagram,
} from "lucide-react";
import "../styles_css/Contact.css";

const Contact = () => {
  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission
  };

  return (
    <div className="contact-container">
      <div className="contact-info">
        <h1 className="title">
          <span className="highlight">We'd Love to</span> Hear From You
        </h1>

        <p className="description">
          For inquiries or further assistance, please contact us through our
          website or reach out directly via email or phone.
        </p>

        <div className="contact-details">
          <div className="contact-item">
            <Phone className="icon" />
            <span>+41767354845</span>
          </div>
          <div className="contact-item">
            <Mail className="icon" />
            <span>office@protecht.ch</span>
          </div>
          <div className="contact-item">
            <MapPin className="icon" />
            <div>
              <p>Zugerstrasse 72, 6340 Baar</p>
              <p>Switzerland</p>
            </div>
          </div>
        </div>

        <div className="social-links">
          <a
            href="https://www.linkedin.com/company/protechtch/posts/?feedView=all"
            className="social-link"
          >
            <Linkedin className="icon" />
          </a>
          <a
            href="https://www.facebook.com/protecht.ch/"
            className="social-link"
          >
            <Facebook className="icon" />
          </a>
          <a
            href="https://www.instagram.com/protecht_ch"
            className="social-link"
          >
            <Instagram className="icon" />
          </a>
        </div>
      </div>

      <div className="contact-form">
        <form onSubmit={handleSubmit} className="form">
          <div className="form-row">
            <input
              type="text"
              placeholder="First name"
              required
              className="input"
            />
            <input
              type="text"
              placeholder="Last name"
              required
              className="input"
            />
          </div>

          <input type="email" placeholder="Email" required className="input" />
          <input type="tel" placeholder="Phone number" className="input" />

          <textarea
            placeholder="Leave a message..."
            rows="6"
            className="input textarea"
          ></textarea>

          <button type="submit" className="submit-button">
            Send message
          </button>
        </form>
      </div>
    </div>
  );
};

export default Contact;
