import React, { useState, useRef, useEffect } from "react";
import "../styles_css/Carousel.css"; // Add custom styles for carousel

const ImageCarousel = ({ images }) => {
  const carouselRef = useRef(null);
  const [isDragging, setIsDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const [scrollLeft, setScrollLeft] = useState(0);

  // Clone the images array for seamless infinite scrolling
  const infiniteImages = [...images, ...images];

  useEffect(() => {
    const carousel = carouselRef.current;

    const handleScroll = () => {
      const totalScrollWidth = carousel.scrollWidth; // Total width of the content
      const visibleWidth = carousel.offsetWidth; // Width of the visible area

      console.log("Scroll Position:", carousel.scrollLeft);
      console.log("Scroll Width:", totalScrollWidth);
      console.log("Visible Width:", visibleWidth);

      // Reset to the start when scrolling reaches the end
      if (carousel.scrollLeft >= totalScrollWidth - visibleWidth) {
        console.log("Reached the end, resetting to the middle...");
        carousel.scrollLeft = carousel.scrollLeft - totalScrollWidth / 2;
      }

      // Reset to the end when scrolling reaches the start
      if (carousel.scrollLeft <= 0) {
        console.log("Reached the start, resetting to the middle...");
        carousel.scrollLeft = totalScrollWidth / 2;
      }
    };

    carousel.addEventListener("scroll", handleScroll);

    // Start carousel in the middle of the cloned images
    carousel.scrollLeft = carousel.scrollWidth / 2;

    console.log("Initial Scroll Position:", carousel.scrollLeft);

    return () => {
      carousel.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartX(e.pageX - carouselRef.current.offsetLeft);
    setScrollLeft(carouselRef.current.scrollLeft);
    console.log("Mouse Down:", {
      startX: e.pageX,
      scrollLeft: carouselRef.current.scrollLeft,
    });
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;
    e.preventDefault();
    const x = e.pageX - carouselRef.current.offsetLeft;
    const walk = x - startX; // Distance dragged
    carouselRef.current.scrollLeft = scrollLeft - walk;
    console.log("Dragging:", {
      currentX: x,
      walk,
      newScrollLeft: carouselRef.current.scrollLeft,
    });
  };

  const handleMouseUp = () => {
    setIsDragging(false);
    console.log("Mouse Up, dragging stopped");
  };

  const handleMouseLeave = () => {
    setIsDragging(false);
    console.log("Mouse Left, dragging stopped");
  };

  return (
    <div
      className="carousel"
      ref={carouselRef}
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseLeave}
    >
      {infiniteImages.map((img, index) => (
        <div className="carousel-item" key={index}>
          <img src={img} alt={`Slide ${index}`} />
        </div>
      ))}
    </div>
  );
};

export default ImageCarousel;
