import React, { useState, useEffect, useRef } from "react";
import { NavLink } from "react-router-dom";
import "../styles_css/Header.css";
import logo from "../images/logo.png";
import menuIcon from "../images/menu.svg"; // Add your menu icon image
import Menu from "./Menu"; // Import the Menu component

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const menuRef = useRef(null);

  const toggleMenu = () => {
    setIsMenuOpen((prev) => !prev);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        closeMenu(); // Close menu if clicked outside
      }
    };

    const handleScroll = () => {
      const scrollThreshold = window.innerHeight * 0.2; // 20vh
      if (window.scrollY > scrollThreshold) {
        closeMenu(); // Close menu if scrolled past threshold
      }
    };

    document.addEventListener("click", handleClickOutside);
    window.addEventListener("scroll", handleScroll);

    return () => {
      // Cleanup listeners on unmount
      document.removeEventListener("click", handleClickOutside);
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div style={{ width: "100%", backgroundColor: "black" }}>
      <header className="header">
        {/* Logo Section */}
        <div className="logo">
          <a href="/">
            <img src={logo} alt="Protecht Logo" className="logo-image" />
          </a>
        </div>

        {/* Menu Icon for Mobile */}
        <img
          src={menuIcon}
          alt="Menu Icon"
          className="menu-icon"
          onClick={toggleMenu} // Toggle menu on click
        />

        {/* Navigation Links */}
        <nav className="navv">
          <ul className="nav-links">
            <li>
              <NavLink
                to="/"
                className={({ isActive }) => (isActive ? "active" : "")}
              >
                Home
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/services"
                className={({ isActive }) => (isActive ? "active" : "")}
              >
                Services
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/empowering"
                className={({ isActive }) => (isActive ? "active" : "")}
              >
                Empowering
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/about"
                className={({ isActive }) => (isActive ? "active" : "")}
              >
                About
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/contact"
                className={({ isActive }) => (isActive ? "active" : "")}
              >
                Contact
              </NavLink>
            </li>
          </ul>
        </nav>
      </header>

      {/* Conditionally Render the Menu Component */}
      {isMenuOpen && (
        <div ref={menuRef}>
          <Menu />
        </div>
      )}
    </div>
  );
};

export default Header;
