import React, { useState, useRef, useEffect } from "react";
import "../styles_css/Carousel.css"; // Add custom styles for carousel
const Carousel = ({ images }) => {
  const [isDragging, setIsDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const [scrollLeft, setScrollLeft] = useState(0);
  const carouselRef = useRef(null);

  useEffect(() => {
    const carousel = carouselRef.current;

    const handleScroll = () => {
      const scrollWidth = carousel.scrollWidth; // Total width of the content
      const visibleWidth = carousel.offsetWidth; // Width of the visible area

      // Reset to the start when scrolling reaches the end
      if (carousel.scrollLeft >= scrollWidth - visibleWidth) {
        carousel.scrollLeft = 0;
      }
      // Reset to the end when scrolling reaches the start
      if (carousel.scrollLeft === 0) {
        carousel.scrollLeft = scrollWidth - visibleWidth;
      }
    };

    carousel.addEventListener("scroll", handleScroll);

    return () => {
      carousel.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartX(e.pageX - carouselRef.current.offsetLeft);
    setScrollLeft(carouselRef.current.scrollLeft);
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;
    e.preventDefault();
    const x = e.pageX - carouselRef.current.offsetLeft;
    const walk = x - startX; // Distance dragged
    carouselRef.current.scrollLeft = scrollLeft - walk;
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleMouseLeave = () => {
    setIsDragging(false);
  };

  return (
    <div
      className="carousel"
      ref={carouselRef}
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseLeave}
    >
      {images.map((img, index) => (
        <div className="carousel-item" key={index}>
          <img src={img} alt={`Slide ${index}`} />
        </div>
      ))}
    </div>
  );
};

export default Carousel;
