import React, { useState } from "react";

import "../styles_css/WhyChooseUs.css";

const App = () => {
  const [activeSection, setActiveSection] = useState(0);

  const sections = [
    {
      title: "Team",
      description: "Our close-knit team is built on trust...",
      percentage: 25,
    },
    {
      title: "Communication",
      description: "We believe in maintaining transparent...",
      percentage: 50,
    },
    {
      title: "Solution",
      description: "Our core focus revolves around finding...",
      percentage: 75,
    },
    {
      title: "Quality",
      description: "Our focus is on finding solutions...",
      percentage: 100,
    },
  ];

  return (
    <div className="container">
      <div
        className="progress-bar"
        style={{ height: `${sections[activeSection].percentage}%` }}
      ></div>

      <div className="tabs">
        {sections.map((section, index) => (
          <div
            key={index}
            className={`tab ${activeSection === index ? "active" : ""}`}
            onMouseEnter={() => setActiveSection(index)}
          >
            {section.title}
          </div>
        ))}
      </div>

      <div className="content">
        {sections.map((section, index) => (
          <div
            key={index}
            className={`content-item ${
              activeSection === index ? "visible" : ""
            }`}
          >
            <h2>{section.title}</h2>
            <p>{section.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
