import React, { useEffect, useRef, useState } from "react";
import shadow1 from "../images/shadow1.png";
import shadow2 from "../images/shadow2.png";
import shadow3 from "../images/shadow3.png";
import "../styles_css/Shadows.css";

const Shadows = () => {
  const headerRef = useRef(null); // Use this if Header is inside this component
  const [dynamicHeight, setDynamicHeight] = useState(0);

  const adjustShadowHeights = () => {
    const header = document.querySelector(".header"); // Select Header dynamically
    const hero = document.querySelector(".hero-section"); // Select Hero dynamically

    if (header && hero) {
      const headerHeight = header.offsetHeight;
      const heroHeight = hero.offsetHeight;
      setDynamicHeight(headerHeight + heroHeight);
    }
  };

  useEffect(() => {
    adjustShadowHeights();

    window.addEventListener("resize", adjustShadowHeights);
    return () => window.removeEventListener("resize", adjustShadowHeights);
  }, []);

  return (
    <>
      <div
        className="blue-shadow1"
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          height: `${dynamicHeight}px`,
          width: "30%",
          backgroundImage: `url(${shadow1})`,
          backgroundSize: "cover",
        }}
      ></div>

      <div
        className="blue-shadow2"
        style={{
          position: "absolute",
          top: 0,
          right: 0,
          height: `${dynamicHeight}px`,
          width: "20%",
          backgroundImage: `url(${shadow2})`,
          backgroundSize: "cover",
        }}
      ></div>

      <div
        className="blue-shadow3"
        style={{
          position: "absolute",
          top: 0,
          right: "20%",
          height: `${dynamicHeight}px`,
          width: "40%",
          backgroundImage: `url(${shadow3})`,
          backgroundSize: "cover",
        }}
      ></div>
    </>
  );
};

export default Shadows;
