import React, { useState } from "react";
import "../styles_css/WhyChooseUs.css";
function App() {
  const [activeSection, setActiveSection] = useState(0);

  const sections = [
    {
      title: "Team",
      description:
        "Our close-knit team is built on trust, collaboration, and shared goals. We support each other through challenges, value open communication, and celebrate each other's successes. This unity creates an environment where everyone feels empowered to contribute their best, knowing they are part of a supportive and dedicated team committed to achieving common goals. This dynamic approach keeps us motivated, helping us grow stronger both as individuals and as a team.",
      percentage: 25,
    },
    {
      title: "Communication",
      description:
        "We believe in maintaining transparent and effective communication channels. Our approach ensures that every team member and client is always informed and engaged in the process, leading to better outcomes and stronger relationships.",
      percentage: 50,
    },
    {
      title: "Solution",
      description:
        "Our core focus revolves around finding innovative and effective solutions to complex challenges. We combine creativity with technical expertise to deliver results that exceed expectations and drive real business value.",
      percentage: 75,
    },
    {
      title: "Quality",
      description:
        "Quality is at the heart of everything we do. From initial planning to final delivery, we maintain rigorous standards and attention to detail, ensuring that every project meets our high benchmarks for excellence.",
      percentage: 100,
    },
  ];

  return (
    <div className="min-h-screen bg-white py-20 px-4 md:px-8">
      <h1 className="text-4xl md:text-5xl text-gray-700 font-light mb-16 max-w-7xl mx-auto">
        Why Choose Us?
      </h1>

      <div className="max-w-7xl mx-auto flex flex-col md:flex-row gap-8 relative">
        <div className="relative md:w-1/3">
          <div className="w-1 md:h-full bg-gray-200 absolute left-0 top-0">
            <div
              className="w-full bg-[#4285f4] transition-all duration-300 ease-in-out"
              style={{ height: `${sections[activeSection].percentage}%` }}
            />
          </div>

          <div className="flex flex-col gap-8 pl-8">
            {sections.map((section, index) => (
              <button
                key={index}
                className={`text-left text-xl transition-all duration-300 ${
                  activeSection === index
                    ? "text-[#4285f4] font-normal"
                    : "text-gray-500 font-light"
                }`}
                onMouseEnter={() => setActiveSection(index)}
              >
                {section.title}
              </button>
            ))}
          </div>
        </div>

        <div className="md:w-2/3">
          {sections.map((section, index) => (
            <div
              key={index}
              className={`transition-all duration-300 absolute w-full ${
                activeSection === index
                  ? "opacity-100 visible"
                  : "opacity-0 invisible"
              }`}
            >
              <div className="bg-[#4285f4] text-white p-8 rounded-lg">
                <h2 className="text-2xl mb-4">{section.title}</h2>
                <p className="text-lg leading-relaxed">{section.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
